	// |B| = |D| = C((m - 1)n, k)
	ans -= C(m * (n - 1), k), ans += p, ans %= p;
	ans -= C(m * (n - 1), k), ans += p, ans %= p;
	ans -= C((m - 1) * n, k), ans += p, ans %= p;
	ans -= C((m - 1) * n, k), ans += p, ans %= p;

	// |AB| = |BC| = |CD| = |DA| = C((m - 1)(n - 1), k)
	// |AC| = C(m(n - 2), k)
	// |BD| = C((m - 2)n, k)
	ans += C((m - 1) * (n - 1), k), ans %= p;
	ans += C((m - 1) * (n - 1), k), ans %= p;
	ans += C((m - 1) * (n - 1), k), ans %= p;
	ans += C((m - 1) * (n - 1), k), ans %= p;
	ans += C(m * (n - 2), k), ans %= p;
	ans += C((m - 2) * n, k), ans %= p;

	// |ABC| = |ADC| = C((m - 1)(n - 2), k)
	// |ABD| = |CBD| = C((m - 2)(n - 1), k)
	ans -= C((m - 1) * (n - 2), k), ans += p, ans %= p;
	ans -= C((m - 1) * (n - 2), k), ans += p, ans %= p;
	ans -= C((m - 2) * (n - 1), k), ans += p, ans %= p;
	ans -= C((m - 2) * (n - 1), k), ans += p, ans %= p;

