	std::vector<int> v;
	ans = divide(v, n);

	// printf("f(%d) = %lld\n", n, ans);
	return ans;
}

int main() {
	for (int n; ~scanf("%d", &n) && n != 0; printf("%lld\n", n == 1 ? 1 : solve(n) * 2));
	return 0;
}
