    freopen("menci_table.in", "r", stdin);
    freopen("menci_table.out", "w", stdout);

    int t;
    scanf("%d", &t);
    while (t--) {
        long long n, m, k;
        scanf("%lld %lld %lld %lld", &n, &m, &k, &p);
