				for (int i = 0; i < m; i++) {
					assert(n / l * i < MAXM_EXTENDED);
					assert(p - a + i < MAXM_EXTENDED);
					long long t = omega[n / l * i] * p[i + m] % MOD;
					// printf("use omega = %lld\n", omega[n / l * i]);
					p[i + m] = (p[i] - t + MOD) % MOD;
					(p[i] += t) %= MOD;
				}
