	}

	void idft(long long *a, const int n) {
		transform(a, n, omegaInverse);
		long long x = inv(n, MOD);
		for (int i = 0; i < n; i++) a[i] = a[i] * x % MOD;
	}

	void operator()(long long *a, long long *b, const int n) {
		assert(n <= MAXM_EXTENDED);
		/*
