		scanf("%d", &N[i].w);
	}

	split();

	while (m--) {
		int s, t;
		scanf("%d %d", &s, &t);
		play(&N[s], &N[t]);
	}

	solve();

	for (int i = 1; i <= n; i++) printf("%d%c", N[i].ans, i == n ? '\n' : ' ');

	fclose(stdin);
	fclose(stdout);

	return 0;
}
