		else printf("dpUp[%d] = { nullptr, %d }, ", i, a[i].dpUp.x);
		if (a[i].dpInline.prec) printf("dpInline[%d] = { (%d, %d), %d }\n", i, a[i].dpInline.prec->x, a[i].dpInline.prec->y, a[i].dpInline.x);
		else printf("dpInline[%d] = { nullptr, %d }\n", i, a[i].dpInline.x);
