			// printf("Connecting [%d, %d, %d, %d] ...\n", e.u + 1, e.v + 1, e.a, e.b);
			lct.link(e.u, e.v, i, e.b);
		}

		if (lct.test(0, n - 1)) ans = std::min(ans, e.a + E[lct.queryMax(0, n - 1)].b);
	}

	printf("%d\n", ans == INT_MAX ? -1 : ans);

	return 0;
}
