	std::vector<Trie::Node *> vec;
	n = t.init(op, vec);
	t.build();

	buildFailTree();
	dfs();

	int m;
	static int ans[MAXN];
	scanf("%d", &m);
	for (int i = 0; i < m; i++) {
		int x, y;
		scanf("%d %d", &x, &y), x--, y--;
		vec[y]->q.push_back(Trie::Node::Query(vec[x], &ans[i]));
	}

	solve();
	for (int i = 0; i < m; i++) printf("%d\n", ans[i]);

	return 0;
}
