	_bit1 = &bit1, _bit2 = &bit2;
#endif
	while (n--) {
		int cmd;
		scanf("%d", &cmd);
		if (cmd == 1) {
			int t, c, l, r, d;
			scanf("%d %d %d %d %d", &t, &c, &l, &r, &d);
			add(t, c, l, r, d);
		} else if (cmd == 2) {
			int t, l, r;
			scanf("%d %d %d", &t, &l, &r);
			printf("%d\n", query(t, l, r));
		} else {
			int t, c;
			scanf("%d %d", &t, &c);
			del(c);
		}
	}

	return 0;
}
```

