		x1 = 2 * len - r + t, y1 = l - r, x2 = 2 * len + t - 1, y2 = len + r;
		transform(x1, y1, 2), transform(x2, y2, 2);
		if (r == len) x1++;
		ans += bit2.query(x1, y1, x2, y2);
	}
	return ans;
#endif
}

int main() {
