#ifndef FORCE_BIT
	bit1.init(len * 4 + 1, len * 4 + 1);
	bit2.init(len * 4 + 1, len * 4 + 1);
	_bit1 = &bit1, _bit2 = &bit2;
#endif
	while (n--) {
