        Q[i].u--, Q[i].v--;
    }

    // forceSolve();
    solve();

    for (int i = 0; i < q; i++) {
        // assert(Q[i].solved);
        // printf("%d ", Q[i].cnt);
        puts(Q[i].ans ? "Yes" : "No");
    }

    fclose(stdin);
    fclose(stdout);

    return 0;
}
