```c++
#include <cstdio>
#include <cstring>

const int MAXT = 10;
const int MAXN = 2000;
const int MOD = 1000000007;
const int CHARSET_SIZE = 26;

int mem[MAXN + 1][2][2][2][CHARSET_SIZE + 1];
bool calced[MAXN + 1][2][2][2][CHARSET_SIZE + 1];

