    int &ans = mem[n][c2EqualToC1][c3EqualToC2][c4EqualToC3][c4];
    if (calced[n][c2EqualToC1][c3EqualToC2][c4EqualToC3][c4]) return ans;
    calced[n][c2EqualToC1][c3EqualToC2][c4EqualToC3][c4] = true;

