        addEdge(u, v);
    }

    toposort();

    int ans = 0;
    for (int i = 0; i < n; i++) {
        if (N[i].outDegree == 0 && N[i].originInDegree != 0) ans += N[i].cnt;
    }

    printf("%d\n", ans);

    fclose(stdin);
    fclose(stdout);

    return 0;
}
