	q.push(s);

	while (!q.empty()) {
		unsigned int v = q.front();
		q.pop();

		if (v == t) return dist[v];

		for (int i = 0; i < 4; i++) {
			for (int j = 0; j < 4; j++) {
				int a = 4 * i + j;
				unsigned int va = !!(v & (1 << a));

				if (i != 3) {
					int b = 4 * (i + 1) + j;
					unsigned int vb = !!(v & (1 << b));
					if (va != vb) {
						unsigned int u = v;
						if (vb) u |= (1 << a);
						else u &= ~(1 << a);

						if (va) u |= (1 << b);
						else u &= ~(1 << b);

						if (dist[u] > dist[v] + 1) {
							dist[u] = dist[v] + 1;
							q.push(u);
						}
					}
				}
				if (j != 3) {
					int b = 4 * i + (j + 1);
					unsigned int vb = !!(v & (1 << b));
					if (va != vb) {
						unsigned int u = v;
						if (vb) u |= (1 << a);
						else u &= ~(1 << a);

						if (va) u |= (1 << b);
						else u &= ~(1 << b);

						if (dist[u] > dist[v] + 1) {
							dist[u] = dist[v] + 1;
							q.push(u);
						}
					}
				}
			}
		}
	}

	return -1;
}

int main() {
	unsigned int s = read(), t = read();
	printf("%d\n", bfs(s, t));
	return 0;
}
