	}

	Rectangle rect = getBound();

	int l = 0, r = std::max(rect.x2 - rect.x1, rect.y2 - rect.y1);
	while (l < r) {
		int mid = l + (r - l) / 2;
		if (check(mid)) r = mid;
		else l = mid + 1;
	}

	printf("%d\n", l);

	return 0;
}
