	for (int i = 1; i <= n; i++) {
		if (!nodes[i].visited) {
			printPath(&nodes[i]);
			putchar('\n');
		}
	}

	printf("%d\n", n - maxMatch);

	fclose(stdin);
	fclose(stdout);

	return 0;
}
