			ans[i - 1][j - 1] = std::min(ans[i - 1][j - 1], search(i - 1, j - 1) + 1);
			ans[i - 1][j - 1] = std::min(ans[i - 1][j - 1], search(i - 1, j) + 1);
			ans[i - 1][j - 1] = std::min(ans[i - 1][j - 1], search(i, j - 1) + 1);
		}

		calced[i - 1][j - 1] = true;
	}

	return ans[i - 1][j - 1];
}

int main() {
	scanf("%s %s", s1, s2);
	n1 = strlen(s1), n2 = strlen(s2);

	printf("%d\n", search(n1, n2));
	return 0;
}
```

