		const int w = 1 - x;

		for (int j = -n; j <= n; j++) {
			if (j - w >= -n && j - w <= n && f[i - 1][j - w] != INT_MIN) f[i][j] = std::max(f[i - 1][j], f[i - 1][j - w] + v);
			else if (j - w > n) f[i][j] = std::max(f[i - 1][j], f[i - 1][n] + v);
			else f[i][j] = f[i - 1][j];
			// printf("f[%d][%d] = %d\n", i, j, f[i][j]);
		}
	}

	int ans = 0;
	for (int i = -n; i <= 1; i++) ans = std::max(ans, f[n][i]);

	printf("%d\n", ans);

	return 0;
}
```

