	for (int i = 0; i < n; i++) {
		printf("%d %d %s\n", belong[i], ht[i], &s[sa[i]]);
	}
#endif

	std::vector<int> v[MAXLEN];
	int maxH = 0;
	for (int i = 0; i < n; i++) v[ht[i]].push_back(i), maxH = std::max(maxH, ht[i]);
	for (int i = maxH; i > 0; i--) {
