	long long operator()(const int s, const int t, const int n) {
		long long res = 0;
		while (makeLevelGraph(&N[s], &N[t], n)) {
			long long flow;
			while ((flow = findPath(&N[s], &N[t])) > 0) res += flow;
		}

		return res;
	}
} dinic;

int n;
bool flag[MAXN][MAXN];

template <typename T> inline T sqr(const T x) { return x * x; }

template <typename T>
inline bool isSquare(const T x) {
	T t = static_cast<T>(sqrt(static_cast<double>(x)));
	return t * t == x;
}

template <typename T>
inline T gcd(const T a, const T b) {
	return !b ? a : gcd(b, a % b);
}

inline void color() {
	for (int i = 1; i <= n; i++) {
		if (N[i].v) continue;

		std::queue<Node *> q;
		q.push(&N[i]);
		N[i].color = true;
		N[i].v = true;

		while (!q.empty()) {
			Node *v = q.front();
			q.pop();

			for (Edge *e = v->e; e; e = e->next) {
				if (!e->t->v) {
					e->t->v = true;
					e->t->color = !v->color;
					q.push(e->t);
				} else assert(e->t->color != v->color);
			}
		}
	}
}

inline void clean() {
	for (int i = 1; i <= n; i++) {
		Edge *next;
		for (Edge *&e = N[i].e; e; next = e->next, delete e, e = next);
	}
}

int main() {
	scanf("%d", &n);
	long long sum = 0;
	for (int i = 1; i <= n; i++) {
		scanf("%lld", &N[i].x);
		sum += N[i].x;
	}

	for (int i = 1; i <= n; i++) {
		for (int j = i + 1; j <= n; j++) {
			if (isSquare(sqr(N[i].x) + sqr(N[j].x)) && gcd(N[i].x, N[j].x) == 1) {
				flag[i][j] = flag[j][i] = true;
				addEdge(i, j, 0);
			}
		}
	}

	color();
	clean();

	const int s = 0, t = n + 1;
	for (int i = 1; i <= n; i++) {
		if (N[i].color) addEdge(s, i, N[i].x);
		else addEdge(i, t, N[i].x);

		for (int j = i + 1; j <= n; j++) {
			if (flag[i][j]) {
				if (N[i].color) addEdge(i, j, LLONG_MAX);
				else addEdge(j, i, LLONG_MAX);
			}
		}
	}

	long long maxFlow = dinic(s, t, n + 2);
	printf("%lld\n", sum - maxFlow);

	return 0;
}
```

