```c++
#include <cstdio>
#include <climits>
#include <cassert>
#include <algorithm>

const int MAXN = 50000;
const int MAXM = 50000;

int n, m, a[MAXN];

struct Splay {
	enum Relation {
		L = 0, R = 1
	};

	struct Node {
		Node *c[2], *p, **r;
		int size, cnt, val;

		Node(Node *p, const int val, Node **r) : p(p), r(r), size(1), cnt(1), val(val) {
			c[L] = c[R] = NULL;
		}

		~Node() {
			if (c[L]) delete c[L];
			if (c[R]) delete c[R];
		}

		void maintain() {
			size = cnt;
			if (c[L]) size += c[L]->size;
			if (c[R]) size += c[R]->size;
		}

		Relation relatain() const {
			return this == p->c[L] ? L : R;
		}

		void rotate() {
			Relation x = relatain();
			Node *o = p;

			p = o->p;
			if (o->p) o->p->c[o->relatain()] = this;

			o->c[x] = c[x ^ 1];
			if (c[x ^ 1]) c[x ^ 1]->p = o;

			c[x ^ 1] = o;
			o->p = this;

			o->maintain(), maintain();

			if (!p) *r = this;
		}

		Node *splay(Node *targetParent = NULL) {
			while (p != targetParent) {
				if (p->p == targetParent) rotate();
				else if (p->relatain() == relatain()) p->rotate(), rotate();
				else rotate(), rotate();
			}
			return this;
		}

		Node *prec() {
			splay();
			Node *v = c[L];
			while (v->c[R]) v = v->c[R];
			return v->splay();
		}

		Node *succ() {
			splay();
			Node *v = c[R];
			while (v->c[L]) v = v->c[L];
			return v->splay();
		}

		int left() const { return c[L] ? c[L]->size : 0; }

#ifdef DBG
		void validate() {
			int size = 0;
			if (c[L]) c[L]->validate(), assert(this->val > c[L]->val), size += c[L]->size;
			if (c[R]) c[R]->validate(), assert(this->val < c[R]->val), size += c[R]->size;
			assert(this->size == size + cnt);
		}

		void print(const int depth = 0);
#endif
	} *r;
#ifdef DBG
	int id;
#endif

	Splay(const int *a, const int n) : r(NULL) {
		insert(INT_MAX), insert(INT_MIN);
#ifdef DBG
		static int time = 0;
		time++;
		id = time;
		printf("build(%d): ", id);
		for (int i = 0; i < n; i++) printf("%d%c", a[i], i == n - 1 ? '\n' : ' ');
#endif
		for (int i = 0; i < n; i++) insert(a[i]);
	}

	~Splay() { delete r; }

	Node *find(const int x) {
		Node *v = r;
		while (v && x != v->val) {
			if (x < v->val) v = v->c[L];
			else v = v->c[R];
		}
		return v ? v->splay() : NULL;
	}

	Node *insert(const int x) {
		Node *v = find(x);
		if (v) {
			v->cnt++, v->size++;
			return v;
		}

		Node **target = &r, *p = NULL;
		while (*target) {
			p = *target;
			p->size++;
			if (x< p->val) target = &p->c[L];
			else target = &p->c[R];
		}

		return (*target = new Node(p, x, &r))->splay();
	}

	int rank(const int x) {
		Node *v = find(x);
		int res;
		if (v) res = v->left();
		else {
			v = insert(x);
			res = v->left();
			erase(v);
		}
#ifdef DBG
		printf("rank(%d) in (%d) = %d\n", x, id, res);
#endif
		return res;
	}

	Node *select(const int k) {
		int x = k + 1;
		Node *v = r;
		while (!(x >= v->left() + 1 && x <= v->left() + v->cnt)) {
			if (x < v->left() + 1) v = v->c[L];
			else x -= v->left() + v->cnt, v = v->c[R];
		}
		return v->splay();
	}

	void erase(Node *v) {
		if (v->cnt != 1) {
			v->cnt--, v->size--;
			return;
		}

		Node *prec = v->prec(), *succ = v->succ();

		prec->splay();
		succ->splay(prec);

		delete succ->c[L];
		succ->c[L] = NULL;

		succ->maintain(), prec->maintain();
	}

	void erase(const int x) {
		Node *v = find(x);
		erase(v);
	}

	int prec(const int x) {
		Node *v = find(x);
		if (v) return v->prec()->val;
		else {
			v = insert(x);
			int res = v->prec()->val;
			erase(v);
			return res;
		}
	}

	int succ(const int x) {
		Node *v = find(x);
		if (v) return v->succ()->val;
		else {
			v = insert(x);
			int res = v->succ()->val;
			erase(v);
			return res;
		}
	}

#ifdef DBG
	void validate() {
		r->validate();
	}
#endif
};

