### 代码
```cpp
#include <cstdio>
#include <cstring>
#include <vector>
#include <algorithm>

const int MAXN = 500000;
const int MAXM = 200000 * 4;

enum OperateType {
	Update = 1, Query = 2
};

struct Operate {
	OperateType type;
	int id, x, y, *ans, num;

	Operate(OperateType type, int id, int x, int y, int num, int *ans = NULL) : type(type), id(id), x(x), y(y), ans(ans), num(num) {}
