		if (S[i].v.inDegree == 0) k++;
	}

	for (int i = 0; i < cnt; i++) {
		if (S[i].v.inDegree == 0 && S[i].s == 1) {
			bool flag = false;
			for (Edge *e = S[i].v.e; e; e = e->next) {
				if (e->t->inDegree <= 1) {
					flag = true;
					break;
				}
			}
			if (!flag) {
				k--;
				break;
			}
		}
	}

	printf("%.6lf\n", static_cast<double>(n - k) / n);

	return 0;
}
```

