		Node *v = q.front();
		q.pop();

		cnt++;
		a[v->dist %= 3]++;

		for (Edge *e = v->e; e; e = e->next) if (!e->t->solved && e->t != v->parent) {
			e->t->parent = v;
			e->t->dist = v->dist + e->w;
			q.push(e->t);
		}
	}

	return a[0] * a[0] + a[1] * a[2] * 2;
}

inline int solve() {
	std::stack<Node *> s;
	s.push(&N[0]);

	int res = 0;
	while (!s.empty()) {
		Node *v = s.top();
		s.pop();

		Node *root = center(v);
		res += calc(root);
		root->solved = true;

		for (Edge *e = root->e; e; e = e->next) if (!e->t->solved) {
			res -= calc(e->t, e->w);
			s.push(e->t);
		}
	}

	return res;
}

int main() {
	scanf("%d", &n);
	for (int i = 0; i < n - 1; i++) {
		int u, v, w;
		scanf("%d %d %d", &u, &v, &w), u--, v--, w %= 3;
		addEdge(u, v, w);
	}

	int cnt = solve(), sum = n * n, g = std::__gcd(cnt, sum);
	printf("%d/%d\n", cnt / g, sum / g);

	return 0;
}
