#include <cstdio>
#include <cstdlib>
#include <ctime>
#include <algorithm>

const int MAXN = 500000;
const int MAXM = 500000;

struct UnionFindSet {
	int a[MAXN];

	void init(int n) {
		for (int i = 0; i < n; i++) a[i] = i;
	}

	int find(int x) {
		return a[x] == x ? x : a[x] = find(a[x]);
	}

	void merge(int x, int y) {
		a[find(x)] = find(y);
	}
};

