	srand((time(NULL) << 16) | ((clock() << 16) >> 16) ^ x ^ MAGIC_NUMBER);
	return (rand() ^ x) % (r - l + 1) + l;
}

int main() {
	int n = MAXN, m = MAXM;

	static UnionFindSet ufs;
	ufs.init(n);

	printf("%d %d\n", n, m);

	for (int i = 0; i < n - 1; ) {
		int u, v;
