			ufs.merge(u - 1, v - 1);
			printf("%d %d\n", u, v);
			i++;
		}
	}

	for (int i = 0; i < m; i++) {
		int a, b, c;
		a = rand(1, n), b = rand(1, n), c = rand(1, n);

		printf("%d %d %d\n", a, b, c);
	}

	return 0;
}
