		}

		return;
		for (std::list<Block>::const_iterator b = blocks.begin(); b != blocks.end(); b++) {
			for (std::list<int>::const_iterator it = b->l.begin(); it != b->l.end(); it++) {
				dprintf("%d ", *it + 1);
			}
		}
		dprintf("%c", '\n');
	}
} list;

int main() {
	// freopen("array.in", "r", stdin);
	// freopen("array.out", "w", stdout);

