		read(n), read(k);

		if (k == 1) write(n);
		else if (k == 2) write((long long)ceil((sqrt(8 * n + 1) - 1) / 2));
		else if (k == 3) {
			int ans = std::lower_bound(f2[k], f2[k] + cnt[k], n) - f2[k];
